package guvi;
import java.util.Scanner;
public class PowerOfANum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int num=sc.nextInt();
		int pow=sc.nextInt();
		System.out.println("The power is:"+Math.pow(num, pow));

	}

}
